DROP TABLE IF EXISTS `bhm_goods`;
CREATE TABLE `bhm_goods`  (
  `goods_id` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '商品ID',
  `goods_cate_id` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '商品分类ID',
  `shop_id` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '店铺ID',
  `name` varchar(48) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '商品名字',
  `title` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '商品标题',
  `main_image` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '商品主图',
  `goods_status` int(11) NOT NULL DEFAULT 0 COMMENT '商品状态:0-正常;1-已删除;2-下架;3-申请上架;4-拒绝;5-上架',
  `favorite_num` int(11) NOT NULL DEFAULT 0 COMMENT '收藏次数',
  `visit_count` int(11) NOT NULL DEFAULT 0 COMMENT '浏览次数',
  `sort_num` int(11) NOT NULL AUTO_INCREMENT COMMENT '平台商品排序',
  `shop_sort_num` int(11) NOT NULL DEFAULT 0 COMMENT '商家商品排序',
  `send_area` varchar(1024) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '配送区域',
  `remark` varchar(256) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '备注',
  `expire_time` datetime(0) NULL DEFAULT NULL COMMENT '过期时间',
  `comment` int(11) NULL DEFAULT 0 COMMENT '评论次数',
  `sale` int(11) NULL DEFAULT 0 COMMENT '销量',
  `allow_type` int(11) NOT NULL DEFAULT 1 COMMENT '允许购买用户:1-全部用户;2-VIP用户',
  `is_shop_show` int(11) NOT NULL DEFAULT 0 COMMENT '商城可见:0-否;1-是',
  `is_new` int(11) NOT NULL DEFAULT 0 COMMENT '新品:0-否;1-是',
  `is_hot` int(11) NOT NULL DEFAULT 0 COMMENT '热门:0-否;1-是',
  `is_flag` int(11) NOT NULL DEFAULT 0 COMMENT '推荐:0-否;1-是',
  `refund_reason` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '拒绝上架理由',
  `out_reason` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '下架理由',
  `apply_time` datetime(0) NULL DEFAULT NULL COMMENT '申请上架时间',
  `down_time` datetime(0) NULL DEFAULT NULL COMMENT '下架时间',
  `up_time` datetime(0) NULL DEFAULT NULL COMMENT '上架时间',
  `add_time` datetime(0) NOT NULL COMMENT '新增时间',
  `edit_time` datetime(0) NOT NULL COMMENT '修改时间',
  `good_desc` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '商品描述',
  `is_index` int(11) NULL DEFAULT 0 COMMENT '是否首页推广0:否1：是',
  `is_vip_discount` int(11) NULL DEFAULT 0 COMMENT '是否是会员折扣商品0:否；1:是',
  PRIMARY KEY (`goods_id`) USING BTREE,
  INDEX `FK_bhm_goods_goods_cate`(`goods_cate_id`) USING BTREE,
  INDEX `sort_num_index`(`sort_num`) USING BTREE,
  CONSTRAINT `FK_bhm_goods_goods_cate` FOREIGN KEY (`goods_cate_id`) REFERENCES `bhm_goods_cate` (`goods_cate_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB AUTO_INCREMENT = 5651 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '商品' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of bhm_goods
-- ----------------------------

-- ----------------------------
-- Table structure for bhm_goods_cate
-- ----------------------------
DROP TABLE IF EXISTS `bhm_goods_cate`;
CREATE TABLE `bhm_goods_cate`  (
  `goods_cate_id` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '商品分类ID',
  `prnt_cate_id` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '父分类ID',
  `name` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '分类名字',
  `sort_num` int(11) NOT NULL AUTO_INCREMENT COMMENT '排序',
  `image` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '分类图片',
  `level` int(11) NOT NULL COMMENT '级别',
  `is_valid` int(11) NOT NULL DEFAULT 1 COMMENT '是否删除:0-否;1-是',
  `add_time` datetime(0) NOT NULL COMMENT '新增时间',
  `edit_time` datetime(0) NOT NULL COMMENT '修改时间',
  `IS_EFFITIVE` int(11) NOT NULL DEFAULT 0 COMMENT '有效:0-是;1-否',
  PRIMARY KEY (`goods_cate_id`) USING BTREE,
  INDEX `sort_index`(`sort_num`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 4503 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '商品分类' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of bhm_goods_cate
-- ----------------------------

-- ----------------------------
-- Table structure for bhm_goods_cmmt
-- ----------------------------
DROP TABLE IF EXISTS `bhm_goods_cmmt`;
CREATE TABLE `bhm_goods_cmmt`  (
  `cmmt_id` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '商品评论ID',
  `goods_id` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '商品ID',
  `shop_id` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '店铺ID',
  `user_id` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '用户ID',
  `order_sku_id` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '商品订单ID',
  `content` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '内容',
  `cmmt_image` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '评论图片',
  `comm_num` int(11) NOT NULL DEFAULT 0 COMMENT '评论次数',
  `is_show_name` int(11) NOT NULL DEFAULT 0 COMMENT '匿名:0-否;1-是',
  `is_show_cmmt` int(11) NOT NULL DEFAULT 0 COMMENT '隐藏评论:0-否;1-是',
  `is_del` int(11) NOT NULL DEFAULT 0 COMMENT '删除:0-否;1-是',
  `cmmt_star` int(11) NOT NULL DEFAULT 1 COMMENT '评论星级:1- 一星级;2-二星级;3-三星级;4-四星级;5五星级',
  `comm_level` int(11) NOT NULL DEFAULT 1 COMMENT '评价等级:1-好评;2-中评;3-差评',
  `beautiful_degree` int(11) NOT NULL DEFAULT 5 COMMENT '美观度星级:1- 一星级;2-二星级;3-三星级;4-四星级;5五星级',
  `taste_degree` int(11) NOT NULL DEFAULT 5 COMMENT '口感度星级:1- 一星级;2-二星级;3-三星级;4-四星级;5五星级',
  `add_time` datetime(0) NOT NULL COMMENT '新增时间',
  `edit_time` datetime(0) NOT NULL COMMENT '修改时间',
  PRIMARY KEY (`cmmt_id`) USING BTREE,
  INDEX `FK_bhm_goods_goods_cmmt`(`goods_id`) USING BTREE,
  CONSTRAINT `FK_bhm_goods_goods_cmmt` FOREIGN KEY (`goods_id`) REFERENCES `bhm_goods` (`goods_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '商品评论' ROW_FORMAT = Dynamic;

