-- ----------------------------
-- Table structure for bhm_order
-- ----------------------------
DROP TABLE IF EXISTS `bhm_order`;
CREATE TABLE `bhm_order`  (
  `order_id` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '订单ID',
  `user_id` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '用户ID',
  `prnt_order_id` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '0' COMMENT '父订单ID（0-为父订单;其他:父订单Id）',
  `have_child` int(11) NOT NULL DEFAULT 0 COMMENT '有子级:0-有;1-无',
  `shop_id` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '0' COMMENT '店铺ID',
  `coupon_id` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '0' COMMENT '优惠券ID',
  `separate_order` int(11) NOT NULL DEFAULT 0 COMMENT '已拆单:0-未拆封;1-已拆分',
  `pay_price` decimal(8, 2) NOT NULL DEFAULT 0 COMMENT '支付总金额',
  `total_price` decimal(8, 2) NOT NULL DEFAULT 0 COMMENT '商品总金额',
  `coupon_price` decimal(8, 2) NULL DEFAULT 0 COMMENT '优惠券抵扣金额',
  `delivery_price` decimal(8, 2) NOT NULL DEFAULT 0 COMMENT '邮费',
  `order_num` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '订单号',
  `payment_num` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '第三方支付单号',
  `delivery_way` int(11) NOT NULL DEFAULT 0 COMMENT '配送方式:0-商家自配;1-快递物流',
  `order_status` int(11) NOT NULL DEFAULT 1 COMMENT '订单状态:1-待付款;2-待发货;3-备货中;4-已发货;5-待评价;6-已评价;7-已取消;',
  `is_del` int(11) NOT NULL DEFAULT 0 COMMENT '是否删除:0-正常;1-已删除',
  `order_type` int(11) NOT NULL DEFAULT 1 COMMENT '订单类型:1-普通订单;2-其他(以后用到再加)',
  `order_note` varchar(256) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '订单留言',
  `pay_time` datetime(0) NULL DEFAULT NULL COMMENT '支付完成时间',
  `pay_type` int(11) NOT NULL DEFAULT 1 COMMENT '支付方式:1-微信;2-钱包;3-支付宝; 4-银联;',
  `send_time` datetime(0) NULL DEFAULT NULL COMMENT '发货时间',
  `receive_type` int(11) NOT NULL DEFAULT 1 COMMENT '确认收货类型:1-用户确认收货;2-系统确认收货',
  `receive_time` datetime(0) NULL DEFAULT NULL COMMENT '用户确认收货时间',
  `full_addr` varchar(256) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '完整地址',
  `username` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '收件人姓名',
  `mobile` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '手机号码',
  `expr_code` varchar(48) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '物流公司缩写',
  `expr_name` varchar(48) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '物流公司',
