CREATE FUNCTION `getChildLssssst`(rootId varchar(1000))
 RETURNS varchar(1000) CHARSET utf8mb4
  READS SQL DATA 
BEGIN
  DECLARE sTemp VARCHAR(1000);

  DECLARE sTempChd VARCHAR(1000);

  SET sTemp = '$';

  SET sTempChd =cast(rootId as CHAR);

  WHILE sTempChd is not null DO

    SET sTemp = concat(sTemp,',',sTempChd);   

    SELECT group_concat(goods_cate_id)    
 INTO sTempChd FROM bhm_goods_cate where FIND_IN_SET(prnt_cate_id,sTempChd)>0;   

  END WHILE;

  RETURN sTemp;    

END
;;
